mapper.select:
SELECT * FROM prompt


        AND prompt_type = #{promptType}


        AND prompt_role = #{promptRole}


        AND enabled = #{enabled}


        AND response_format = #{responseFormat}


        AND created_by = #{createdBy}


        AND (prompt_name ILIKE CONCAT('%', #{keyword}, '%') OR remark ILIKE CONCAT('%', #{keyword}, '%'))


        AND created_time >= #{startTimeStart}


        AND created_time <= #{startTimeEnd}


        AND updated_time >= #{updateTimeStart}


        AND updated_time <= #{updateTimeEnd}


ORDER BY created_time DESC
